<?php/** * Include Styles and Scripts * * @package WordPress * @subpackage alla */// Load jQueryif ( ! function_exists( 'core_mods' ) ) {	function core_mods() {		if ( ! is_admin() ) {			wp_deregister_script('jquery');			wp_register_script('jquery', ( "http://ajax.googleapis.com/ajax/libs/jquery/1.11.0/jquery.min.js" ), array(), '1.11', false);			wp_enqueue_script('jquery');		}	}	core_mods();}/** * Register and enqueue some theme scripts and styles * */function theme_scripts_styles() {	wp_register_script( 'theme_vendor', get_template_directory_uri() . '/scripts/jquery.min.js', array( 'jquery' ), '1.0', true );	wp_register_script( 'theme_cf', get_template_directory_uri() . '/scripts/jquery.carouFredSel-6.2.0-packed.js', array( 'jquery', 'theme_vendor' ), '1.0', true );	wp_register_script( 'theme_common', get_template_directory_uri() . '/scripts/alla.js', array( 'jquery', 'theme_vendor' ), '1.0', true );	wp_enqueue_script( 'theme_vendor' );	wp_enqueue_script( 'theme_cf' );	wp_enqueue_script( 'theme_common' );    wp_register_style( 'site_styles', get_template_directory_uri() . '/alla.css' );	wp_enqueue_style( 'site_styles' );	wp_enqueue_style( 'prefix-font-awesome', 'https://maxcdn.bootstrapcdn.com/font-awesome/4.6.3/css/font-awesome.min.css', array(), '4.0.3' );};add_action( 'wp_enqueue_scripts', 'theme_scripts_styles' );